new Vue({el:"#app",data:{f_store:"",f_store_error:!0,f_store_ErrMsg:"",f_name:"",f_name_error:!0,f_name_ErrMsg:"",f_phone:"",f_phone_error:!0,f_phone_ErrMsg:"",f_amount:"",f_amount_error:!0,f_amount_ErrMsg:"",f_payment:"digital payment",f_payment_error:!0,f_payment_ErrMsg:""},watch:{f_name:function(){/^[\u4e00-\u9fa5a-zA-Z]+$/.test(this.f_name)?(this.f_name_error=!1,this.f_name_ErrMsg=""):(this.f_name_error=!0,this.f_name_ErrMsg="請輸入中文或英文")},f_phone:function(){/^09\d{8}$/.test(this.f_phone)?(this.f_phone_error=!1,this.f_phone_ErrMsg=""):(this.f_phone_error=!0,this.f_phone_ErrMsg="請輸入正確的電話號碼")},f_amount:function(){/^[0-9]+$/.test(this.f_amount)?(this.f_amount_error=!1,this.f_amount_ErrMsg=""):(this.f_amount_error=!0,this.f_amount_ErrMsg="輸入金額錯誤")},f_store:function(){""==this.f_store?this.f_store_ErrMsg="請選擇位於清單內的商店":this.f_store_error=!1},f_payment:function(){"digital payment"==this.f_payment?this.f_payment_ErrMsg="請選擇付款方式":this.f_payment_error=!1}},methods:{signUp:function(){1!=this.f_store_error&&1!=this.f_name_error&&1!=this.f_phone_error&&1!=this.f_amount_error&&1!=this.f_payment_error?alert("資料成功送出"):alert("送出失敗，請確認資料填寫無誤")},resetIfInvalid:function(){if(""!=this.f_store){for(var r=document.querySelector("#store").children,t=0;t<r.length;t++)if(console.log(r[t].value),this.f_store==r[t].value)return;this.f_store=""}}}});